import React, { useEffect, useState } from 'react'
import LoadingPage from './LoadingPage'
import { getTopicData, createPost } from '../utils/api'
import { validatePost } from '../utils/utils'
import file from '../images/file.png'




const url = 'http://127.0.0.1:8000/api'

function CreatePost() {
    const [newPost, setNewPost] = useState(
        {
            image:'',
            topic:'',
            title:'',
            content:''
        }
    )
    const [topics, setTopics] = useState(null)
    const [isError, setIsError] = useState(false)
    const [isLoading, setIsLoading] = useState(true)
    const [missingValue, setMissingValue] = useState(false)

    const handleSubmit = async(e)=> {
        e.preventDefault()
        const form = new FormData()
        const obj = validatePost(newPost)
        if(obj) {
            setMissingValue(obj)
        }else {
            try {
                const keys = Object.keys(newPost)
                keys.forEach((key)=>{
                    if(key === 'image') {
                        form.append('image', newPost[key][0])
                        console.log(newPost[key][0])
                    }else {
                        form.append(key, newPost[key])
                    }
                })
                const data = await createPost(`${url}/create/`, form)
                console.log(data)
            } catch (error) {
                console.log(error)
            }
        }
    }

    function handleChange(e) {
        const {name, value} = e.target 
        name === 'image' ? setNewPost((prev)=>({...prev, [name]:e.target.files})) :
        setNewPost((prev)=>({...prev, [name]:value}))
    }

    const getData = async()=> {
        try {
            const data = await getTopicData(`${url}/topics/`)
            const topicArray = data.map((topic)=>topic.name)
            setTopics(topicArray)
            setTimeout(()=>{
                setIsLoading(false)
            }, 500)
        } catch ({message}) {
            console.log(message)
            setIsLoading(false)
            setIsError(true)
        }
    }

    useEffect(()=>{
        getData()
    }, [])


    if(isLoading) {
        return (
            <LoadingPage />
        )
    }
    if(isError) {
        return (
            <h2>There was an error!</h2>
        )
    }
    return (
        <div className='create-post-container'>
            <div className="create-post__form-container">
                <h2>Create Post</h2>
                <form className="create-post__form" onSubmit={handleSubmit}>
                    <label className='create-post__img-input-label' htmlFor="image">
                        Choose Image
                        <input
                            onChange={handleChange}
                            id='image' 
                            className='create-post__img-input' 
                            type="file" 
                            accept='image/*'
                            name='image'
                            hidden
                        />
                    </label>
                    <label htmlFor="topic">Topic</label>
                    <select
                        id='topic'
                        onChange={handleChange} 
                        className='create-post__select' 
                        name="topic"
                        style={{width:'100%'}}>
                    <option className='create-post__option' value="">--------</option>
                        {topics.map((topic)=>{
                            return (
                                <option className='create-post__option' key={topic} value={topic}>{topic}</option>
                            )
                        })}
                    </select>
                    <label htmlFor="title">Title</label>
                    <input
                        id='title'
                        onChange={handleChange}
                        className='create-post__input' 
                        type="text"
                        value= {newPost.title}
                        placeholder='Title' 
                        name='title'
                    />
                    <label htmlFor="content">Content</label>
                    <textarea
                        id='content'
                        onChange={handleChange}
                        name="content" 
                        className='create-post__textarea'
                        cols="30" 
                        rows="10"
                        value= {newPost.content}
                        placeholder='Content'
                    />
                    <button className='create-post__btn' type='submit'>Submit</button>
                </form>
            </div>
        </div>
    )
}

export default CreatePost
