import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { getPostData } from '../utils/api'
import LoadingPage from './LoadingPage'




const url = 'http://127.0.0.1:8000/api/posts/'

function PostList() {
  const [posts, setPosts] = useState(null)
  const [isError, setIsError] = useState(false)
  const [isLoading, setIsLoading] = useState(true)

  const getData = async()=> {
    try {
      const data = await getPostData(url)
      const objs = data.map((post)=>({...post, date_posted:new Date().toDateString()}))
      setPosts(objs)
      setTimeout(()=>{
        setIsLoading(false)
      }, 500)
    } catch ({message}) {
      setIsError(message)
      setIsLoading(false)
    }
  }

  useEffect(()=>{
    getData()
  }, [])

  if(isLoading) {
    return (
      <LoadingPage />
    )
  }
  if(isError) {
    return (
      <h2>There was an error</h2>
    )
  }
  return (
    <div className='post-container'>
      <h1 className='post-container__post-header'>Posts</h1>
      <div className="post-container__posts">
        {posts.map((post)=>{
          return (
            <div key={post.id} className="post-container__post">
              <img className='post-container__post-image' src={post.image_url} alt={post.title} />
              <div className='post-container__author-and-date'>
                <p className='post-container__date-posted'>{post.date_posted}</p>
                <p className='post-container__post-author'>{post.author}</p>
              </div>

              <h3 className='post-container__post-title'>{post.title}</h3>
              <p className='post-container__post-content'>{post.content.substring(0, 100)}...</p>
              <Link className='post-container__post-read-more' to={`/post/${post.id}/detail/`}>
                <button>Read More</button>
              </Link>
              <button className='post-container__post-topic' style={{justifySelf:'start'}}>
                <span>{post.topic}</span>
                <i className="fa fa-chevron-right"></i>
              </button>
            </div>
          )
        })}
      </div>
    </div>
  )
}

export default PostList
