export const validatePost = (newPost)=> {
    const keys = Object.keys(newPost)
    const reduce = keys.reduce((postObj, item)=>{
        const key = !newPost[item] && item
        if(key){
            return {...postObj,[key]:'' }
        }else {
            return postObj
        }
    }, {})
    return Object.keys(reduce).length  === 0 ? '' : reduce
}


export const windowResizeEvent = function(e, setShowUserMenu, setShowNavLinks, ) {
    setShowUserMenu(false)
    setShowNavLinks(true)
    const navbarWrapper = document.querySelector('.navbar-wrapper ')
    navbarWrapper && setNabarWidth(navbarWrapper.offsetWidth)
    window.removeEventListener('resize', windowResizeEvent)
}
