import React, { useState, useEffect} from 'react'
import { useParams, Link } from 'react-router-dom'
import { getPostData } from '../utils/api'
import LoadingPage from './LoadingPage'



const url = 'http://127.0.0.1:8000/api/post/'


function PostDetail() {
    const [post, setPost] = useState(null)
    const [isError, setIsError] = useState(false)
    const [isLoading, setIsLoading] = useState(true)
    const { id } = useParams()


    useEffect(()=>{
        const getData = async()=> {
          try {
            const data = await getPostData(`${url}${id}/detail/`)
            const objs = {...data, date_posted:new Date().toDateString()}
            setPost(objs)
            setIsLoading(false)
          } catch ({message}) {
            console.log('Error:',message)
            setIsError(message)
            setIsLoading(false)
          }
        }
        getData()
      }, [id])

    if(isLoading) {
        return (
            <LoadingPage />
        )
    }
    if(isError) {
        return (
            <h2>There was an error</h2>
        )
    }
    return (
        <React.Fragment>
            <div className="bg-img"></div>
            <div className='post-detail-container'>
                <div className="post-detail-container__back-to-posts">
                    {/* <i className="fa fa-chevron-left"></i> */}
                    <Link to=''>Back to Posts</Link>
                </div>
                <div className="post-detail-container__post-detail">
                    <div className="post-detail-container__post-image-container">
                        <img className='post-detail-container__post-image' src={post.image_url} alt="" />
                        <div className="post-detail-container__post-image-color-overlay"></div>
                    </div>
                    <div className="post-detail-container__text-contents">
                        <div className="post-detail-container__author-and-date">
                            <h4 className='post-detail-container__post-author'>{post.author}</h4>
                            <p className='post-detail-container__date-posted'>{post.date_posted}</p>
                        </div>
                        <h3 className='post-detail-container__post-title'>{post.title}</h3>
                        <p className='post-detail-container__post-content'>{post.content}</p>
                        <div className="post-detail-container__like-and-reply">
                            {post.like.length > 1 ? 
                                <button className='post-detail-container__post-like' title='give it a clap'>
                                    <i className="fa-solid fa-hands-clapping post-detail-like"></i>
                                    <span>{post.like.length} </span>
                                </button>
                            : 
                                <button className='post-detail-container__post-like' title='give it a clap'>
                                    <i className="fa-solid fa-hands-clapping post-detail-like"></i>
                                    <span>{post.like.length}</span>
                                </button>
                            }
                            <button className='post-detail-reply'>
                                <i className="fa fa-reply" title='reply'></i>
                            </button>
                        </div>
                    </div>
                    <Link to={`/posts/?filter=${post.topic}`} className='post-detail-container__post-topic'>
                        <span>{post.topic}</span>
                        <i className="fa fa-chevron-right"></i>
                    </Link>
                </div>
            </div>
        </React.Fragment>
    )
}

export default PostDetail
