import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { getTopicData } from './api'





const url = 'http://127.0.0.1:8000/api/posts/topics/'

function PostTopics() {
    const [topics, setTopics] = useState(null)
    const [isError, setIsError] = useState(false)
    const [isLoading, setIsLoading] = useState(true)

    const getTopics = async()=> {
        try {
            const data = await getTopicData(url)
            setTopics(data)
            setIsLoading(false)
        } catch ({message}) {
            console.log(message)
            setIsError(message)
        }
    }

    useEffect(()=> {
        getTopics()
    }, [])

    if(isLoading) {
        return (
            <h2>Loading...</h2>
        )
    }
    if(isError) {
        return (
            <h2>There was an error</h2>
        )
    }
    return (
        <div className='container'>
            <h1>Topics</h1>
            <div className="topics-container">
                {topics.map((topic)=> {
                    return (
                        <div key={topic.id} className="topic">
                            <img className='topic-image' src={topic.image_url} alt={topic.name} />
                            <div className='topic-title-container'>
                                <h3 className='topic-name'>{topic.name}</h3>
                                {/* <div className='topic-total-post'>
                                    <p>{topic.total_post}</p>
                                    <p>{topic.total_post > 1 ? 'Post' : 'Post'}</p>
                                </div> */}
                            </div>
                            <p className='topic-description'>{topic.description}</p>
                            <Link className='topic-see-all-post-btn'>See Posts</Link>
                        </div>
                    )
                })}
            </div>
        </div>
    )
}

export default PostTopics
