export const getTopicData = async(url)=> {
    const resp = await fetch(url, {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json'
        },
        body: null
    })
    const data = resp.json()
    return data
}


export const getPostData = async(url)=> {
    const resp = await fetch(url, {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json'
        },
        body: null
    })
    const data = await resp.json()
    return data
}


export const createPost = async(url, form)=>  {
    const resp =  await fetch(url, {
        method: 'POST', 
        headers: {
            // 'Content-Type': 'multipart/form-data'
            'Authorization': 'Token 60601ef4d4c16f5ae5683accf079d35f89faad9e'
        },
        body: form
    })
    const data = await resp.json()
    return data
}
