import React, { useState, useEffect } from 'react'
import { useLocation, Navigate, Link } from 'react-router-dom'
import { getPostData, getTopicData } from '../utils/api'



const topicsUrl = 'http://127.0.0.1:8000/api/topics/'
const postsUrl = 'http://127.0.0.1:8000/api/posts/'

function TopicPosts() {
    const [postArray, setPostArray] = useState(null)
    const [topicsArray, setTopicsArray] = useState(null)
    const [topic, setTopic] = useState(null)
    const {state} = useLocation()

     const getPosts =  async()=> {
        try {
            const data = await getPostData(postsUrl)
            setPostArray(data)
        } catch (error) {
            console.log(error.message)
        }
    }

    const getTopics = async()=> {
        try {
            const data = await getTopicData(topicsUrl)
            const topics = data.reduce((total, topic)=> {
                if(!total.includes(topic.name)){
                    total.push(topic.name)
                }
                return total
            }, ['All Posts'])
            setTopicsArray(topics)

        } catch (error) {
            console.log(error.message)
        }
    }

    const posts = postArray && topic && postArray.filter((post)=> post.topic === state.topic)

    useEffect(()=> {
        getPosts()
        if(state) {
            setTopic(state.topic)
        }
    }, [])

    useEffect(()=> {
        getTopics()
    }, [])

    console.log(topic)

    if(topic && topic.toLowerCase() === 'all posts') {
        return (
            <Navigate to='/posts'/>
        )
    }
    return (
        <div className='topic-posts-container'>
            <div className="topic-posts-container__topics-btns" style={{display:'flex', gap:'1rem'}}>
                {topicsArray && topicsArray.map((obj)=> {
                    return (
                        <Link to={`./?filter=${obj}`} state={{topic:obj}} key={obj}>{obj}</Link>
                    )
                })}
            </div>
        </div>
    )
}

export default TopicPosts
