import React, { useEffect, useState } from 'react'
import { getTopicData } from './api'





const url = 'http://127.0.0.1:8000/api/posts/topics/'

function CreatePost() {
    const [topics, setTopics] = useState(null)
    const [isError, setIsError] = useState(false)
    const [isLoading, setIsLoading] = useState(true)

    const getData = async()=> {
        try {
            const data = await getTopicData(url)
            const topicArray = data.map((topic)=>topic.name)
            setTopics(topicArray)
            setIsLoading(false)
        } catch ({message}) {
            console.log(message)
            setIsLoading(false)
        }
    }

    useEffect(()=>{
        getData()
    }, [])

    if(isLoading) {
        return (
            <h2>Loading...</h2>
        )
    }
    if(isError) {
        return (
            <h2>There was an error!</h2>
        )
    }
    return (
        <div className='create-post-container'>
            <div className="create-post__form-container">
                <form className="create-post__form">
                    <label htmlFor="file">
                        <input id='file' className='create-post__input' type="file" />
                    </label>
                    <select className='create-post__select' name="" id="" style={{width:'100%'}}>
                    <option className='create-post__option' value="">--------</option>
                        {topics.map((topic)=>{
                            return (
                                <option className='create-post__option' key={topic} value="">{topic}</option>
                            )
                        })}
                    </select>
                    <input className='create-post__input' type="text" />
                    <input className='create-post__input' type="text" />

                    <button className='create-post__btn' type='button'>Submit</button>
                </form>
            </div>
        </div>
    )
}

export default CreatePost
