import React, { useState, useEffect } from 'react'
import { getTopicData } from './api'





const url = 'http://127.0.0.1:8000/api/posts/topics/'

function PostTopics() {
    const [topics, setTopics] = useState(null)
    const [isError, setIsError] = useState(false)
    const [isLoading, setIsLoading] = useState(true)

    const getTopics = async()=> {
        try {
            const data = await getTopicData(url)
            setTopics(data)
            setIsLoading(false)
        } catch ({message}) {
            console.log(message)
        }
    }

    useEffect(()=> {
        getTopics()
    }, [])

    if(isLoading) {
        return (
            <h2>Loading...</h2>
        )
    }
    if(isError) {
        return (
            <h2>There was an error</h2>
        )
    }
    return (
        <div className='container'>
            <h1>Topics</h1>
            <div className="topics-container">
                {topics.map((topic)=> {
                    return (
                        <div key={topic.id} className="topic">
                            <img src={topic.image_url} alt={topic.name} />
                            <h3>{topic.name}</h3>
                            <p>{topic.description}</p>
                            <h4>Total post: {topic.total_post}</h4>

                        </div>
                    )
                })}
            </div>
        </div>
    )
}

export default PostTopics
