import React, { useState, useEffect } from 'react'





const container = {
    width: '90%',
    maxWidth: '700px',
    margin: '0 auto',
    border: '1px solid red',
}
const postForm = {
    display: 'grid',
    gap: '1rem',
    alignItems: 'start',
}
const imgLabel = {
    justifySelf: 'start',
    padding: '0.5rem 1rem',
    background:'purple',
    color: 'white',
    borderRadius:'5px'
}

export const PostForm = ()=> {

    return (
        <div style={container}>
            <form>
                <label style={imgLabel} htmlFor="image">
                    Choose Image 
                    <input id='image' type="file" accept='image/*' name='image' hidden/>
                </label>

            </form>
        </div>
    )
}
