import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { getPostData } from '../utils/api'
import LoadingPage from './LoadingPage'
import { PostForm } from './PostForm'
import PostTopics from './PostTopics'


const url = 'http://127.0.0.1:8000/api/posts/'

function PostList() {
  const [posts, setPosts] = useState(null)
  const [isError, setIsError] = useState(false)
  const [isLoading, setIsLoading] = useState(true)

  const getData = async()=> {
    try {
      const data = await getPostData(url)
      const objs = data.map((post)=>({...post, date_posted:new Date().toDateString()}))
      setPosts(objs)
      setTimeout(()=>{
        setIsLoading(false)
      }, 500)
    } catch ({message}) {
      setIsError(message)
      setIsLoading(false)
    }
  }

  useEffect(()=>{
    getData()
  }, [])

  if(isLoading) {
    return (
      <LoadingPage />
    )
  }
  if(isError) {
    return (
      <h2>There was an error</h2>
    )
  }
  return (
    <React.Fragment>
      <div className="bg-img"></div>
      {/* <div className="post-list-create-post-btn-container">
        <button className='post-list-create-post'>Create Post</button>
      </div> */}
      <div className="post-list-main-container">
        <div className='post-container'>
          {/* <h1 className='post-container__post-header'>Posts</h1> */}
          <div className="post-container__posts">
            {posts.map((post)=>{
              return (
                <div key={post.id} className="post-container__post">
                  <div className="post-container__post-image-container">
                    <img className='post-container__post-image' src={post.image_url} alt={post.title} />
                    <div className="post-container__post-image-color-overlay"></div>
                  </div>

                  <div className='post-container__author-and-date'>
                    <h4 className='post-container__post-author'>{post.author}</h4>
                    <div className="post-container__date-and-like">
                      <p className='post-container__date-posted'>{post.date_posted}</p>
                      {post.like.length > 1 ? 
                        <p className='post-container__post-like'>
                          <i class="fa-solid fa-hands-clapping post-like"></i>
                          <span>{post.like.length}</span>
                        </p>
                      : 
                        <p className='post-container__post-like'>
                          <i class="fa-solid fa-hands-clapping post-like"></i>
                          <span>{post.like.length}</span>
                        </p>
                      }
                    </div>
                  </div>

                  <h3 className='post-container__post-title'>{post.title}</h3>
                  <p className='post-container__post-content'>{post.content.substring(0, 100)}...</p>
                  <Link className='post-container__post-read-more-btn' to={`/post/${post.id}/detail/`}>
                    Read More
                  </Link>
                  <Link to={`/posts/?filter=${post.topic}`} className='post-container__post-topic'>
                    <span>{post.topic}</span>
                    <i className="fa fa-chevron-right"></i>
                  </Link>
                </div>
              )
            })}
          </div>
        </div>
        <PostTopics />
      </div>
    </React.Fragment>
  )
}

export default PostList
