import React, { useState, useEffect } from 'react'
import { useLocation, useNavigate } from 'react-router-dom'
import { getTopicData } from '../utils/api'
import { url } from './PostList'
import CreatePost from './CreatePost'


const UpdatePost= ()=> {
    const [topics, setTopics] = useState(null)
    const [error, setError] = useState(null)
    const [post, setPost] = useState(null)
    const {state} = useLocation()
    const navigate = useNavigate()

    const fetchTopics = async()=> {
        try {
            const data = await getTopicData(`${url}/api/topics`)
            const objs = data.map((topic)=>topic.name)
            setTopics(objs)
        } catch ({message}) {
            setError(message)
            navigate('/error', {state:{message:message}})
        }
    }

    useEffect(()=> {
        fetchTopics()
        const obj = {
            image:state.post.image,
            topic:state.post.topic,
            title:state.post.title,
            content:state.post.content
        }
        setPost(obj)
    }, [state.post])

    return (
        <div className='post-form-container'>
            {/* <form className='post-form-container__post-form'>
