import React, { useEffect, useState } from 'react'
import { Link, useLocation, Navigate } from 'react-router-dom'
import { checkLoginInfoValid } from '../utils/utils'
import { login } from '../utils/api'


const url = 'http://127.0.0.1:8000/api/auth/login/'

function Login() {
    const [user, setUser] = useState({username:'',password:''})
    const [message, setMessage] = useState(null)
    const [backendAuthError, setBackendAuthError] = useState(null)
    const [auth, setAuth] = useState(null)
    const [successMessage, setSuccessMessage] = useState(null) 
    const {state} = useLocation()


    const handleForm = async function(e) {
        e.preventDefault()
        setBackendAuthError(null)
        setMessage(null)

        const isValid = checkLoginInfoValid(user)
        if(isValid) {
            try {
                const data = await login(url, user)
                if(data.error) {
                    setBackendAuthError({error:data.error})

                }else {
                    const userAuth = {token:data.token, username:data.username}
                    localStorage.setItem('auth', JSON.stringify(userAuth))
                    setSuccessMessage({success:data.message})
                    setAuth(userAuth)
                }
            } catch (error) {
                console.log(error.message)
                setMessage({error:error.message})
            }
        }else {
            setMessage({error:'All fields are required.'})
        }
    }

    const handleChange = function(e) {
        const {name, value} = e.target  
        setUser((prev)=> ({...prev, [name]:value}))
    }

    useEffect(()=> {
        state && setSuccessMessage(state.message)
        const id = setTimeout(()=>{
            setSuccessMessage(null)
            window.history.replaceState({state:null}, '', '/login')
            clearTimeout(id)
        }, 5000)

    }, [])

    if(auth) {
        return (
            <Navigate to='/posts' replace={true} state={successMessage} />
        )
    }
    return (
        <div className="user-login-main-container">
             {successMessage && <p className='user-login__message'>{successMessage}</p>}
            <div className='user-login'>
                <h2 className='user-login__header'>Login</h2>
                {backendAuthError && <p className='user-register__error'>{backendAuthError.error}</p>}
                {message && <p className='user-register__error'>{message.error}</p>}
                <form className='user-login__form' onSubmit={handleForm}>
                    <input 
                        onChange={handleChange} 
                        value={user.username} 
                        className='user-login__input' 
                        name='username' 
                        type="text" 
                        placeholder='Username'
                    />
                    <input 
                        onChange={handleChange} 
                        value={user.password} 
                        className='user-login__input' 
                        name='password' 
                        type="password" 
                        placeholder='Password'
                    />
                    <button className='user-login__btn' type='submit'>Login</button>
                </form>
                <div className="user-login__not-yet-registered">
                    <p>Not yet registered? </p>
                    <Link to='/register'>Register</Link>
                </div>
            </div>
        </div>
    )
}

export default Login
