import React, { useState, useEffect } from 'react'
import { useLocation, Navigate } from 'react-router-dom'
import { getMyData } from '../utils/api'
import LoadingPage from './LoadingPage'



const url = 'http://127.0.0.1:8000'

function MyPost() {
  const [posts, setPosts] = useState(null)
  const [isError, setIsError] = useState(false)
  const [isLoading, setIsLoading] = useState(true)
  const authenticated = JSON.parse(localStorage.getItem('auth')) || null
  const {state} = useLocation()

  console.log(authenticated.token)

  const getMyPosts = async()=> {
    try {
      const data = await getMyData(`${url}/api/my-post/`, authenticated.token)
      if(data.error) {
        setIsLoading(false)
        setIsError(data)

      }else {
        setPosts(data)
        const timeoutID = setTimeout(()=> {
          setIsLoading(false)
          clearTimeout(timeoutID)
        }, 500)
      }
    } catch (error) {
      console.log(error.message)
    }
  }

  useEffect(()=> {
    getMyPosts()
  }, [])

  if(isLoading) {
    return (
      <LoadingPage />
    )
  }
  if(isError) {
    return (
      <Navigate to='/error' replace={true} state={{message:isError.error}}/>
    )
  }
  return (
    <div className="my-post-main-container">
      <div className='my-post-container'>
          {state && <p className='my-post-container__message'>{state.message}</p>}
          <h1 className='my-post-container__header'>My Posts</h1>
          <div className="my-post-container__posts">

          </div>
      </div>
    </div>

  )
}

export default MyPost
