import React, { useState, useEffect } from 'react'
import { useLocation } from 'react-router-dom'
import { getPostData } from '../utils/api'




const url = 'http://127.0.0.1:8000/api/posts/'

function TopicPosts() {
    const [postArray, setPostArray] = useState(null)
    const [topic, setTopic] = useState(null)
    const {state} = useLocation()

     const getPosts =  async()=> {
        try {
            const data = await getPostData(url)
            setPostArray(data)
        } catch (error) {
            console.log(error.message)
        }
    }

    const posts = postArray &&  postArray.filter((post)=> post.topic === state.topic)

    const topics = postArray && postArray.reduce((total, post)=> {
        if(!total.includes(post.topic)){
            total.push(post.topic)
        }
        return total
    }, ['Posts'])

    useEffect(()=> {
        getPosts()
        setTopic(state.topic)
    }, [])

    console.log(topic)

    return (
        <div className='topic-posts-container'>

        </div>
    )
}

export default TopicPosts
