import React, { useState } from 'react'








function Register() {
    const [newUser, setNewUser] = useState({username:'',password:'',passwordConfirmation:''})



    const handleForm = function(e) {
        e.preventDefault()
        console.log(newUser)
    }

    const handleChange = function(e) {
        const {name, value} = e.target  
        setNewUser((prev)=> ({...prev, [name]:value}))
    }

    return (
        <div className='user-register'>
            <h2 className='user-register__header'>Register</h2>
            <form className='user-register__form' onSubmit={handleForm}>
                <input 
                    onChange={handleChange} 
                    name='username' 
                    value={newUser.username} 
                    type="text" 
                    placeholder='Username'
                />
                <input 
                    onChange={handleChange} 
                    name='password' 
                    value={newUser.password} 
                    type="password" 
                    placeholder='Password'
                />
                <input 
                    onChange={handleChange} 
                    name='passwordConfirmation' 
                    value={newUser.passwordConfirmation} 
                    type="password" 
                    placeholder='Confirm password'
                />
                <button type='submit'>Regster</button>
            </form>
        </div>
    )
}

export default Register
