import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { getPostData } from './api'





const url = 'http://127.0.0.1:8000/api/posts/'

function PostList() {
  const [posts, setPosts] = useState(null)
  const [isError, setIsError] = useState(false)
  const [isLoading, setIsLoading] = useState(true)

  const getData = async()=> {
    try {
      const data = await getPostData(url)
      const objs = data.map((post)=>({...post, date_posted:new Date().toDateString()}))
      setPosts(objs)
      setIsLoading(false)
    } catch ({message}) {
      setIsError(message)
    }
  }

  useEffect(()=>{
    getData()
  }, [])

  if(isLoading) {
    return (
      <h2>Loading...</h2>
    )
  }
  if(isError) {
    return (
      <h2>There was an error</h2>
    )
  }
  return (
    <div className='container'>
      <h1>Posts</h1>
      <div className="post-container">
        {posts.map((post)=>{
          return (
            <div key={post.id} className="post">
              <img src={post.image_url} alt="" />
              <p>Date: {post.date_posted}</p>
              <h4>User: {post.author}</h4>
              <h3>{post.title}</h3>
              <p style={{display:'flex',flexWrap:'wrap'}}>{post.content.substring(0, 100)}...</p>
              <p>Likes: {post.likes.length}</p>
              <Link to={`${post.id}/detail/`}>
                <button>Read More</button>
              </Link>
              <button style={{justifySelf:'start'}}>
                <span>{post.topic}</span>
                <i className="fa fa-chevron-right"></i>
              </button>
            </div>
          )
        })}
      </div>
    </div>
  )
}

export default PostList
