import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { getTopicData } from '../utils/api'
import LoadingPage from './LoadingPage'




const url = 'http://127.0.0.1:8000/api/topics/'

function PostTopics() {
    const [topics, setTopics] = useState(null)
    const [isError, setIsError] = useState(false)
    const [isLoading, setIsLoading] = useState(true)

    const getTopics = async()=> {
        try {
            const data = await getTopicData(url)
            setTopics(data)
            setTimeout(()=>{
                setIsLoading(false)
            }, 500)
        } catch ({message}) {
            console.log(message)
            setIsError(message)
            setIsLoading(false)
        }
    }

    useEffect(()=> {
        getTopics()
    }, [])

    if(isLoading) {
        return (
            <LoadingPage />
        )
    }
    if(isError) {
        return (
            <h2>There was an error</h2>
        )
    }
    return (
        <React.Fragment>
            <div className="bg-img"></div>
            <div className='topics-main-container'>
                <h1 className='topic-main-container__header'>8 Common Dog Health Problems</h1>
                <div className="topics-container">
                    {topics.map((topic)=> {
                        return (
                            <div key={topic.id} className="topic">
                                <img className='topic-image' src={topic.image_url} alt={topic.name} />
                                <div className='topic-title-container'>
                                    <h3 className='topic-name'>{topic.name}</h3>
                                    {topic.total_post > 1 ? 
                                        <p className='topic-post-count'>
                                            <i className="fa-solid fa-message post-container__num-of-post "></i>
                                            {topic.total_post} 
                                        </p>
                                    :
                                        <p className='topic-post-count'>
                                            <i className="fa-solid fa-message post-container__num-of-post "></i>
                                            {topic.total_post} 
                                        </p>
                                    }
                                </div>
                                <p className='topic-description'>{topic.description}</p>
                                {topic.total_post > 1 ?
                                    <Link to={`/posts/?filter=${topic.name}`} className='topic-see-all-post-btn'>See Posts</Link>
                                :
                                    <>
                                        {topic.total_post === 0 ?
                                            <Link className='topic-see-all-post-btn'>No Post to See</Link>
                                        :
                                            <Link to={`/posts/?filter=${topic.name}`} className='topic-see-all-post-btn'>See Post</Link>
                                        }
                                    </>
                                }
                            </div>
                        )
                    })}
                </div>
            </div>
        </React.Fragment>
    )
}

export default PostTopics
