export const getTopicData = async(url)=> {
    const rest = await fetch(url, {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json'
        },
        body: null
    })
}


export const getPostData = async(url)=> {
    const resp = await fetch(url, {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json'
        },
        body: null
    })
    const data = await resp.json()
    return data
}
