import React, { useState, useEffect } from 'react'
import { useLocation, useNavigate } from 'react-router-dom'
import { getTopicData } from '../utils/api'
import { Error } from './Error'
import { url } from './PostList'


const UpdatePost= ()=> {
    const [topics, setTopics] = useState(null)
    const [error, setError] = useState(null)
    const {state} = useLocation()
    const navigate = useNavigate()

    const fetchTopics = async()=> {
        try {
            const data = await getTopicData(`${url}/api/topics`)
            const objs = data.map((topic)=>topic.name)
            setTopics(objs)
        } catch ({message}) {
            setError(message)
            navigate('/error', {state:{message:error}})
        }
    }

    useEffect(()=> {
        fetchTopics()
    }, [state.post])

    return (
        <div className='post-form-container'>
            <form className='post-form-container__post-form'>
                <label className='post-form-container__image-label' htmlFor="image">
                    Choose Image 
                    <input id='image' type="file" accept='image/*' name='image' hidden/>
                </label>
                <label className='post-form-container__label' htmlFor="topic">Topics</label>
                <select className='post-form-container__select' id='topic' name="topic">
                    <option className='post-form-container__option' value="">-----</option>
                </select>
                <label className='post-form-container__label' htmlFor="title">Title</label>
                <input className='post-form-container__input' type="text" name='title' id='title' value='' />
                <label className='post-form-container__label' htmlFor="content">Content</label>
                <textarea className='post-form-container__textarea' name="content" id="content" value='' cols="30" rows="8"></textarea>
            </form>
        </div>
    )
}

export default UpdatePost
