import React, { useState, useEffect } from 'react'
import { Link, useOutletContext} from 'react-router-dom'
import { getTopicData } from '../utils/api'
import LoadingPage from './LoadingPage'




const url = 'http://127.0.0.1:8000/api/topics/'

function PostTopics() {
    const [topics, setTopics] = useState(null)
    const [isError, setIsError] = useState(false)
    const [isLoading, setIsLoading] = useState(true)
    const posts = useOutletContext()

    posts && posts.map((post)=>{
        console.log(post)
    })

    const getTopics = async()=> {
        try {
            const data = await getTopicData(url)
            setTopics(data)
            setTimeout(()=>{
                setIsLoading(false)
            }, 500)
        } catch ({message}) {
            console.log(message)
            setIsError(message)
            setIsLoading(false)
        }
    }

    useEffect(()=> {
        getTopics()
    }, [])

    if(isLoading) {
        return (
            <LoadingPage />
        )
    }
    if(isError) {
        return (
            <h2>There was an error</h2>
        )
    }
    return (
        <React.Fragment>
            <div className="bg-img"></div>
            <div className="topics-parent">
                <div className='topics-main-container'>
                    <h1 className='topics-main-container__header'>8 Common Dog Health Problems</h1>
                    <div className="topics-container">
