    margin: '0 auto',
    border: '1px solid red',
}

export const PostForm = ()=> {

    return (
        <div style={container}>
            <form>
                <label htmlFor="image">
                    Choose Image 
                    <input id='image' type="file" accept='image/*' name='image' hidden/>
                </label>

            </form>
        </div>
    )
}
