import React, { useEffect, useState } from 'react'
import { getTopicData } from './api'





const url = 'http://127.0.0.1:8000/api/posts/topics/'

function CreatePost() {
    const [topics, setTopics] = useState(null)

    const getData = async()=> {
        try {
            const data = await getTopicData(url)
            const topicArray = data.map((topic)=>topic.name)
            setTopics(topicArray)
        } catch ({message}) {
            console.log(message)
        }
    }

    useEffect(()=>{
        getData()
    }, [])
    return (
        <div className='create-post-container'>
            <div className="create-post__form-container">
                <form className="create-post__form">
                    <select name="" id="" style={{width:'100%'}}>
                        {topics.map((topic)=>{
                            return (
                                <option key={topic} value="">{topic}</option>
                            )
                        })}
                    </select>
                    <input type="text" />
                    <input type="text" />
                    <input type="file" />
                    <button type='button'>Submit</button>
                </form>
            </div>
        </div>
    )
}

export default CreatePost
