import React, { useState, useEffect } from 'react'
import { useLocation, Navigate, Link } from 'react-router-dom'
import { getPostData, getTopicData } from '../utils/api'
import LoadingPage from './LoadingPage'
import Error from './Error'


const topicsUrl = 'http://127.0.0.1:8000/api/topic/'
const postsUrl = 'http://127.0.0.1:8000/api/posts/'

function TopicPosts() {
    const [postArray, setPostArray] = useState(null)
    const [topicsArray, setTopicsArray] = useState(null)
    const [isError, setIsError] = useState(false)
    const [isLoading, setIsLoading] = useState(true)
    const {state} = useLocation()


     const getPosts =  async()=> {
        try {
            const data = await getPostData(postsUrl)
            setPostArray(data)

        } catch (error) {
            console.log(error.message)
            setIsLoading(false)
            setIsError(true)
        }
    }

    const getTopics = async()=> {
        try {
            const data = await getTopicData(topicsUrl)
            const topics = data.reduce((total, topic)=> {
                if(!total.includes(topic.name)){
                    total.push(topic.name)
                }
                return total
            }, ['All Posts'])
            setTopicsArray(topics)

        } catch (error) {
            setIsLoading(false)
            setIsError({message:error.message})
            console.log(error.message)
        }
    }

    const posts = postArray && state && postArray.filter((post)=> post.topic === state.topic)

    useEffect(()=> {
        getPosts()
    }, [])

    useEffect(()=> {
        getTopics()
    }, [])


    if(postArray && topicsArray) {
        const timeOutID = setTimeout(()=> {
            setIsLoading(false)
            clearTimeout(timeOutID)
        }, 3000)
    }

    if(state && state.topic.toLowerCase().replaceAll(' ', '') === 'allposts') {
        return (
            <Navigate to='/posts'/>
        )
    }
    if(isLoading) {
        return (
        <LoadingPage />
        )
    }
    if(isError) {
        return (
            <Error to='/error' replace={true} state={isError} />
        )
    }
    return (
        <div className='topic-posts-container'>
            <div className="topic-posts-container__topics-btns" style={{display:'flex', gap:'1rem'}}>
                {topicsArray && topicsArray.map((obj)=> {
                    return (
                        <Link to={`.?filter=${obj}`} state={{topic:obj}} key={obj}>{obj}</Link>
                    )
                })}
            </div>
        </div>
    )
}

export default TopicPosts
