export const validatePost = (newPost)=> {
    const keys = Object.keys(newPost)
    const reduce = keys.reduce((postArray, item)=>{
        const obj = !newPost[item] && {[item]:''}
        postArray.push(obj)
        return postArray
    }, [])
    console.log(reduce)
    return reduce.length > 0 ? reduce : ''
}
